import PropTypes from "prop-types";
import { CaretRightOutlined } from "@ant-design/icons";
import { Collapse, Typography, Table, Image, Divider } from "antd";

function TablesCerts(datas) {
  const columns = [
    {
      title: "Attribut",
      dataIndex: "key",
      key: "key",
    },
    {
      title: "Valeur",
      dataIndex: "value",
      key: "value",
    },
  ];

  const dataSource = Object.entries(datas).map(([key, value]) => ({
    key: key,
    value: value,
  }));

  return (
    <Table
      dataSource={dataSource}
      columns={columns}
      pagination={false}
      size="small"
      style={{
        backgroundColor: "white",
      }}
      showHeader={false}
    />
  );
}

function CertificateDetails({ data }) {
  const items = [];

  Object.entries(data).map(([keys, values]) =>
    items.push({
      key: keys,
      label: (
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: 20,
              width: "90%",
            }}
          >
            <div style={{ width: 50 }}>
              <Image
                src={values.sign[1].face}
                width={50}
                height={50}
                fallback="data:image/png;base64,"
              />
            </div>
            <Divider type="vertical" />

            <Typography.Text strong style={{ color: "#000", marginRight: 20 }}>
              Signature de {values.cert[0]}
            </Typography.Text>
          </div>
          <Image
            fallback="data:image/png;base64,"
            src={"data:image/jpg;base64," + values.sign[1].specimen}
            height={50}
          />
        </div>
      ),
      children: TablesCerts(values.cert[1]),
    })
  );

  return (
    <>
      <Collapse
        accordion={true}
        bordered={false}
        expandIcon={({ isActive }) => (
          <CaretRightOutlined
            style={{ color: "#000" }}
            rotate={isActive ? 90 : 0}
          />
        )}
        expandIconPosition="end"
        style={{
          background: "white",
          boxShadow: "0 0 2px black",
          padding: 0,
        }}
        items={items}
      />
    </>
  );
}

CertificateDetails.propTypes = {
  data: PropTypes.object.isRequired,
};

export default CertificateDetails;
